import discord
import random
from discord import app_commands


class rpgshop(discord.Client):
    def __init__(self):
        intents = discord.Intents.all()
        super().__init__(
            command_prefix="$",
            intents=intents
        )
        self.tree = app_commands.CommandTree(self)

    async def setup_hook(self):
        await self.tree.sync()

    async def on_ready(self):
        print(f"O bot {self.user} está funcionando.")


bot = rpgshop()


@bot.tree.command(name="d", description="Rolar um dado dX")
@app_commands.describe(
    dado="Número de faces no dado:"
)
async def d(interaction: discord.Interaction, dado: int):
    result = random.randint(1, dado)
    await interaction.response.send_message(f"{result}")


