@bot.tree.command(name="calcularoferta", description="Verificar o novo preço com desconto de um determinado item")
@app_commands.describe(
    valor="Valor inteiro do item (em pratas):",
    desconto="Desconto:"
)
async def oferta(interaction: discord.Interaction, valor: int, desconto: int):
    desconto = desconto / 100
    desconto = 1 - desconto
    valor = valor * desconto
    valor = round(valor)
    await interaction.response.send_message(f"Olá, {interaction.user.mention}! \n **O preço do seu item é:**\n **{valor}** (em pratas)")


